import React, {Component} from 'react';
import GetWebsiteData from './getWebsiteData';
import $ from 'jquery'; 

class AddPost extends Component {
    state = {
        url: 'http://engineering.dishantagnihotri.com/mapbox/change-base-map-without-loosing-styles-layers-on-mapbox/'
    };

    fetchWebsiteData = () => {
        const URL = "http://www.whateverorigin.org/get?url=" + encodeURIComponent(this.state.url) + "&callback=?";

        $.getJSON(URL, function(response) {
            console.log(response);
         //   let heading = $(response).find('h1').length

                if (response.http_code === 200) {
                    console.log('Parsing Response');
                    <GetWebsiteData content={response.content} />
                }
        });
    };

    handleInputValue ({ target }) {
        console.log (target);
        this.setState({
            [target.name]: target.value
          });
    }

    render () {
        return (
            <React.Fragment>
                <div className="ui one column stackable center aligned page grid">
                    <div className="ui labeled input">
                        <div className="ui label">
                            URL
                        </div>
                        <input type="text" placeholder="Enter website URL" onChange={this.handleInputValue} />
                        <button className="ui button" onClick={() => this.fetchWebsiteData(this.state.url)}>Get</button>
                    </div>
                </div>
                <GetWebsiteData />
                <br /><br />
            </React.Fragment>
        );
    };
}
export default AddPost;
