import React, {Component} from 'react';

class Login extends Component {
    state = {
        username: '',
        password: ''
    };

    checkLoginInfo = (e) => {
        e.preventDefault();
        console.log(e)
    };

    render () {
        return (
            <div className="ui middle aligned center aligned grid">
                <div className="column" style={{maxWidth: 450}}>
                    <h2 className="ui image header">Log-in to your account</h2>
                    <form className="ui large form" onSubmit={this.checkLoginInfo}>
                        <div className="ui stacked secondary segment">
                            <div className="field">
                                <div className="ui input">
                                    <input type="text" name="username" placeholder="E-mail address" 
                                           value={this.state.username} onChange={(e)=>this.inputChangedHandler(e)} />
                                </div>
                            </div>
                            <div className="field">
                                <div className="ui input">
                                    <input type="password" name="password" placeholder="Password" 
                                           value={this.state.password} onChange={(e)=>this.inputChangedHandler(e)} />
                                </div>
                            </div>
                            <button type="submit" className="ui fluid large teal submit button">Login</button>
                        </div>
                    </form>
                </div>
            </div>
        )
    }
}
export default Login;
