import React, {Component} from 'react';
import GetWebsiteData from './getWebsiteData';
import $ from 'jquery'; 

class AddPost extends Component {
    state = {
        url: ''
    };

    constructor () {
        super();
        this.handleInputValue = this.handleInputValue.bind(this);
    }

    fetchWebsiteData = () => {
        const URL = "http://www.whateverorigin.org/get?url=" + encodeURIComponent(this.state.url) + "&callback=?";

        $.getJSON(URL, function(response) {
            console.log(response);
         //   let heading = $(response).find('h1').length
                console.log(response.status.http_code)
                if (response.status.http_code === 200) {
                    console.log('Parsing Response');
                   // <GetWebsiteData content={response.content} />
                }
        });
    };

    handleInputValue = ({target}) => {
        this.setState({
            url: target.value
        });
    }

    render () {
        return (
            <React.Fragment>
                <div className="ui one column stackable center aligned page grid">
                    <div className="ui labeled input">
                        <div className="ui label">
                            URL
                        </div>
                        <input type="text" placeholder="Enter website URL" value={this.state.url} onChange={this.handleInputValue} />
                        <button className="ui button" onClick={this.fetchWebsiteData}>Get</button>
                    </div>
                </div>
                <GetWebsiteData />
                <br /><br />
            </React.Fragment>
        );
    };
}
export default AddPost;
