import React, {Component} from 'react';
import GetWebsiteData from './getWebsiteData';
import $ from 'jquery'; 

class AddPost extends Component {
    state = {
        url: 'http://engineering.dishantagnihotri.com/mapbox/change-base-map-without-loosing-styles-layers-on-mapbox/',
        response: null,
        content: {
            heading: '',
            description: ''
        }
    };

    constructor () {
        super();
        this.handleInputValue = this.handleInputValue.bind(this);
        this.fetchWebsiteData = this.fetchWebsiteData.bind(this);
    }

    fetchWebsiteData = () => {
        const URL = "http://www.whateverorigin.org/get?url=" + encodeURIComponent(this.state.url) + "&callback=?";

        $.getJSON(URL, function(response) {
            console.log(response);
            let heading = $(response).find('title').length,
                description = $(response).find('p').length

            console.log ('HEADING: ', heading);
            console.log ('DESCRIPTION: ', description);

            this.setState ({
                response: response.status.http_code === 200 ? 200 : 500,
                content: {
                    heading: heading ? heading : 'Not Fetched',
                    description: description ? description : 'Not Fetched'
                }
            })
        }.bind(this));
    };

    handleInputValue = ({target}) => {
        this.setState({
            url: target.value
        });
    }

    render () {
        if (this.state.response === 200) {
            return (
                <React.Fragment>
                    <div className="ui one column stackable center aligned page grid">
                        <div className="ui labeled input">
                            <div className="ui label">
                                URL
                            </div>
                            <input type="text" placeholder="Enter website URL" value={this.state.url} onChange={this.handleInputValue} />
                            <button className="ui button" onClick={this.fetchWebsiteData}>Get</button>
                        </div>
                    </div>
                    <GetWebsiteData content={this.state.content} />
                    <br /><br />
                </React.Fragment>
            );
        } else {
            return (
                <React.Fragment>
                    <div className="ui one column stackable center aligned page grid">
                        <div className="ui labeled input">
                            <div className="ui label">
                                URL
                            </div>
                            <input type="text" placeholder="Enter website URL" value={this.state.url} onChange={this.handleInputValue} />
                            <button className="ui button" onClick={this.fetchWebsiteData}>Get</button>
                        </div>
                    </div>
                    <br /><br />
                </React.Fragment>
            );
        }

    };
}
export default AddPost;
